// defining a function which works with a single String parameter
def depluralizer(word: String) =
   if (word.takeRight(4) == "sses" || word.takeRight(3) == "ies" )
        word.stripSuffix("es")
   else if (word.takeRight(2) == "ss")
     word
   else
     word.stripSuffix("s")

def exclamator(word: String) = word + "!"

depluralizer("businesses")

// save an anonymous function into a value
val removeIng = (word: String) => word.stripSuffix("ing")

removeIng("learning")

def concatenateAll(delimiter: String, args: String*) = {
    args.foldLeft("")((x: String, y: String) => x + delimiter + y)
}
concatenateAll("+","soft", "kitty", "warm", "kitty")

def step1stemmer(word: String): String = {
 word match {
   case i if (i.takeRight(1)=="s") => depluralizer(i)
   case i if (i.takeRight(3)=="ing") => removeIng(i)
   case i if (i.takeRight(2)=="ed") => word.stripSuffix("ed")
   case _ => word
 }
}
step1stemmer("learning")

def factorial(n: Int): Int = {
 n match {
  case 0 | 1 => 1
  case x => x * factorial(n-1)
 }
}

factorial(5)

