def avgCounts(trainLM:CountLM, testLM:CountLM, vocab:Set[String]) = {
  val avgTestCounts = new HashMap[Double,Double] withDefaultValue 0.0
  val norm = new HashMap[Double,Double] withDefaultValue 0.0
  for (ngram <- cross(List.fill(trainLM.order)(vocab.toList))) {
    val trainCount = trainLM.counts(ngram)
    val testCount = testLM.counts(ngram)
    avgTestCounts(trainCount) += testCount
    norm(trainCount) += 1.0
  }
  for (c <- avgTestCounts.keys) avgTestCounts(c) /= norm(c)
  avgTestCounts
}

